package programa;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 22;
		
		/*
		 * Incrementar N en 77.
Decrementarla en 3.
Duplicar su valor.
		 */
		System.out.println("Incrementar: "+ (N+=77));
		System.out.println("Decrementarla en 3 " + (N-=3));
		System.out.println("Duplicar su valor " + (N*=2));
	}

}
